const linksArray=document.querySelectorAll("a[href^='#']");for(let e of linksArray)e.addEventListener("click",o=>{o.preventDefault();const t=e.getAttribute("href");document.querySelector(t).scrollIntoView({behavior:"smooth",block:"start"})});const portfolioNavbar=document.querySelector(".portfolio__lists.nav-list"),portfolioLinks=portfolioNavbar.children,imageBlock=document.querySelector(".portfolio__image-block");console.log(imageBlock.children[0]);for(let e of portfolioLinks)e.addEventListener("click",()=>{removeActiveLink(),e.classList.add("nav-list__item_active"),hiddenImage(e.dataset.category)});function removeActiveLink(){for(let e of portfolioNavbar.children)e.classList.remove("nav-list__item_active")}function hiddenImage(e){for(let o of imageBlock.children)"all"===e||o.dataset.category===e?o.classList.remove("portfolio__image_hidden"):o.classList.add("portfolio__image_hidden")}